import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import ManageSchedules from '../tabs/ManageSchedulesTab';
import ReplyQuestions from '../tabs/ReplyQuestionsTab';

const RepresentativeDashboard = () => {
  const [activeTab, setActiveTab] = useState('manageSchedules');

  const renderTabContent = () => {
    switch (activeTab) {
      case 'manageSchedules':
        return <ManageSchedules />;
      case 'replyQuestions':
        return <ReplyQuestions />;
      case 'viewCustomers':
        return (
          <div>
            <h2>Customers with Reservations</h2>
            <p>Produce a list of customers who have reservations on a given transit line and date.</p>
            {/* You could link to `ViewCustomers` component here if needed */}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      <h1>Representative Dashboard</h1>

      {/* Tabs for customer representative functionality */}
      <div style={{ marginTop: '20px' }}>
        <button
          onClick={() => setActiveTab('manageSchedules')}
          style={{
            background: activeTab === 'manageSchedules' ? 'blue' : 'lightgray',
            color: activeTab === 'manageSchedules' ? 'white' : 'black',
            margin: '0 5px',
            padding: '10px',
          }}
        >
          Manage Schedules
        </button>
        <button
          onClick={() => setActiveTab('replyQuestions')}
          style={{
            background: activeTab === 'replyQuestions' ? 'blue' : 'lightgray',
            color: activeTab === 'replyQuestions' ? 'white' : 'black',
            margin: '0 5px',
            padding: '10px',
          }}
        >
          Reply to Questions
        </button>
        <button
          onClick={() => setActiveTab('viewCustomers')}
          style={{
            background: activeTab === 'viewCustomers' ? 'blue' : 'lightgray',
            color: activeTab === 'viewCustomers' ? 'white' : 'black',
            margin: '0 5px',
            padding: '10px',
          }}
        >
          View Customers
        </button>
      </div>

      {/* Tab Content */}
      <div style={{ marginTop: '20px' }}>{renderTabContent()}</div>
    </div>
  );
};

export default RepresentativeDashboard;
