// src/pages/ManageTrainSchedules.js
import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';

const ManageTrainSchedules = () => {
  const [trainSchedules, setTrainSchedules] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchTrainSchedules();
  }, []);

  // Fetch train schedules
const fetchTrainSchedules = async () => {
    try {
      const response = await axios.get("http://localhost:3000/api/admin/train-schedules", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data && Array.isArray(response.data.trainSchedules)) {
        setTrainSchedules(response.data.trainSchedules);
      } else {
        setTrainSchedules([]); // Set to an empty array if no schedules are found
        setError("No train schedules available.");
      }
    } catch (err) {
      console.error("Error fetching train schedules:", err);
      setError("Failed to fetch train schedules. Try again later.");
    }
  };

  // Implement Add, Edit, Delete functions here (if needed)

  return (
    <div>
      <h2>Manage Train Schedules</h2>
      {error && <p className="error">{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Schedule ID</th>
            <th>Train ID</th>
            <th>Transit Line Name</th>
            <th>Departure DateTime</th>
            <th>Arrival DateTime</th>
            {/* Add actions column if editing/deleting */}
          </tr>
        </thead>
        <tbody>
          {trainSchedules.map((schedule) => (
            <tr key={schedule.ScheduleID}>
              <td>{schedule.ScheduleID}</td>
              <td>{schedule.TrainID}</td>
              <td>{schedule.TransitLineName}</td>
              <td>{schedule.DepartureDateTime}</td>
              <td>{schedule.ArrivalDateTime}</td>
              {/* Add actions (Edit/Delete) buttons if needed */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageTrainSchedules;
