import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";

const MainPage = () => {
  const [stations, setStations] = useState([]);
  const [sourceStation, setSourceStation] = useState("");
  const [destinationStation, setDestinationStation] = useState("");
  const [error, setError] = useState("");

  // Fetch stations from the backend API
  const fetchStations = async () => {
    try {
      const response = await axiosInstance.get("/api/stations");
      setStations(response.data.stations);
    } catch (err) {
      console.error("Error fetching stations:", err);
      setError("Failed to load stations. Please try again.");
    }
  };

  useEffect(() => {
    fetchStations();
  }, []);

  return (
    <div className="main-page">
      <header className="header">
        <h1>Railway Reservation System</h1>
        <div id="real-time-clock"></div>
      </header>

      <div className="search-container">
        <h2>Search Train</h2>
        <select
          value={sourceStation}
          onChange={(e) => setSourceStation(e.target.value)}
        >
          <option value="">--Source Station--</option>
          {stations.map((station) => (
            <option key={station.StationID} value={station.StationName}>
              {station.StationName}
            </option>
          ))}
        </select>

        <select
          value={destinationStation}
          onChange={(e) => setDestinationStation(e.target.value)}
        >
          <option value="">--Destination Station--</option>
          {stations.map((station) => (
            <option key={station.StationID} value={station.StationName}>
              {station.StationName}
            </option>
          ))}
        </select>

        <input type="date" placeholder="Select Date" />

        <button className="search-button">Search</button>

        {error && <p className="error">{error}</p>}
      </div>
    </div>
  );
};

export default MainPage;
