import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';

const ViewCustomersTab = () => {
  const [customers, setCustomers] = useState([]);
  const [error, setError] = useState('');
  const [transitLine, setTransitLine] = useState('');
  const [reservationDate, setReservationDate] = useState('');

  // Fetch customers on component mount or when filters change
  useEffect(() => {
    if (transitLine || reservationDate) {
      fetchCustomers();
    }
  }, [transitLine, reservationDate]);

  // Function to fetch customers with reservations on the given transit line and date
  const fetchCustomers = async () => {
    try {
      const response = await axiosInstance.get('/representative/customers', {
        params: {
          transitLine,
          reservationDate,
        },
      });
      setCustomers(response.data.customers);
      setError('');
    } catch (err) {
      console.error('Error fetching customers:', err);
      setError('Failed to fetch customers.');
    }
  };

  // Function to handle filter changes
  const handleTransitLineChange = (e) => {
    setTransitLine(e.target.value);
  };

  const handleDateChange = (e) => {
    setReservationDate(e.target.value);
  };

  // Function to clear filters
  const clearFilters = () => {
    setTransitLine('');
    setReservationDate('');
    setCustomers([]);
  };

  return (
    <div>
      <h2>View Customers with Reservations</h2>
      {error && <p className="error">{error}</p>}

      {/* Filters Section */}
      <div style={{ marginBottom: '20px' }}>
        <label>
          Transit Line:
          <input
            type="text"
            value={transitLine}
            onChange={handleTransitLineChange}
            placeholder="Enter Transit Line"
            style={{ marginRight: '10px', padding: '5px' }}
          />
        </label>
        <label>
          Reservation Date:
          <input
            type="date"
            value={reservationDate}
            onChange={handleDateChange}
            style={{ marginRight: '10px', padding: '5px' }}
          />
        </label>
        <button onClick={fetchCustomers} style={{ padding: '5px 10px', marginRight: '10px' }}>
          Apply Filter
        </button>
        <button onClick={clearFilters} style={{ padding: '5px 10px' }}>
          Clear Filter
        </button>
      </div>

      {/* Customers Table */}
      <table>
        <thead>
          <tr>
            <th>Customer ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Reservation Date</th>
            <th>Transit Line</th>
          </tr>
        </thead>
        <tbody>
          {customers.length > 0 ? (
            customers.map((customer) => (
              <tr key={customer.CustomerID}>
                <td>{customer.CustomerID}</td>
                <td>{customer.FirstName}</td>
                <td>{customer.LastName}</td>
                <td>{customer.Email}</td>
                <td>{customer.ReservationDate.split('T')[0]}</td>
                <td>{customer.TransitLine}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No customers found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ViewCustomersTab;
