import React, { useState } from "react"; 
import axios from "axios"; 
import { useNavigate } from "react-router-dom";
import "../styles/Login.css"; // Import CSS file
import backgroundImage from "../assets/Railway-Reservation-System.jpg"; // Import the image

const Signup = () => {

  const [email, setEmail] = useState("");
  const [firstName , setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://lcoalhost:3000/api/login", {
        username,
        password,
      });

      const { token, role, id } = response.data;

      localStorage.setItem("token", token);
      localStorage.setItem("role", role.toLowerCase());

      if (role.toLowerCase() === "admin") {
        navigate("/admin-dashboard");
      } else if (role.toLowerCase() === "staff") {
        navigate("/representative-dashboard");
      } else if (role.toLowerCase() === "customer") {
        localStorage.setItem("customerId", id);
        navigate("/customer-dashboard");
      } else {
        setError("Unknown role. Please contact support.");
      }
    } catch (err) {
      setError(err.response?.data?.error || "Login failed. Try again.");
    }
  };

  return (
    <div
      className="login-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      {/* Heading */}
      <h1 className="page-title">Railway Reservation System</h1>

      <form onSubmit={handleLogin}>
        <h2>Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
};

export default Signup;
