import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';
import { jsxDEV as _jsxDEV, Fragment as _Fragment } from "react/jsx-dev-runtime";
const ManageSchedulesTab = () => {
  _s();
  const [schedules, setSchedules] = useState([]);
  const [editingSchedule, setEditingSchedule] = useState(null);
  const [error, setError] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newSchedule, setNewSchedule] = useState({
    transitLineName: '',
    travelTime: '',
    arrivalDate: '',
    arrivalTime: '',
    departureDate: '',
    departureTime: '',
    trainID: '',
    stops: []
  });
  const [availableTrains, setAvailableTrains] = useState([]);
  const [availableStops, setAvailableStops] = useState([]);
  useEffect(() => {
    fetchSchedules();
    fetchTrains();
    fetchStops();
  }, []);

  // Fetch all train schedules
  const fetchSchedules = async () => {
    try {
      const response = await axiosInstance.get('/customer-rep/train-schedules');
      setSchedules(response.data.trainSchedules);
      setError('');
    } catch (err) {
      console.error('Error fetching schedules:', err);
      setError('Failed to fetch schedules.');
    }
  };

  // Fetch all available trains for dropdown
  const fetchTrains = async () => {
    try {
      const response = await axiosInstance.get('/trains');
      setAvailableTrains(response.data.trains);
