import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles.css";

const MainPage = () => {
  const [stations, setStations] = useState([]);
  const [originStation, setOriginStation] = useState("");
  const [destinationStation, setDestinationStation] = useState("");
  const [travelDate, setTravelDate] = useState("");
  const [trainStops, setTrainStops] = useState([]);
  const [error, setError] = useState("");

  // Fetch stations
  useEffect(() => {
    const fetchStations = async () => {
      try {
        const response = await axios.get("http://localhost:3000/api/stations");
        setStations(response.data.stations);
      } catch (err) {
        console.error("Error fetching stations:", err);
      }
    };
    fetchStations();
  }, []);

  // Fetch train stops
  const handleSearch = async () => {
    if (!originStation || !destinationStation || !travelDate) {
      setError("Please fill all fields.");
      return;
    }
    setError("");

    try {
      const response = await axios.get(
        `http://localhost:3000/api/mainPage/trains/${originStation}/detail`
      );
      setTrainStops(response.data.trainStops); // Updated to use trainStops
    } catch (err) {
      console.error("Error fetching train details:", err);
      setError("Failed to fetch train details.");
    }
  };

  return (
    <div className="main-page">
      <div className="header">
        <h1>Railway Reservation System</h1>
        <div className="header-links">
          <a href="/login">Sign In</a>
          <a href="/signup">Sign Up</a>
        </div>
        <div className="time">{new Date().toLocaleTimeString()}</div>
      </div>

      {/* Search Container */}
      <div className="search-container">
        <h2>Search Train</h2>
        <select value={originStation} onChange={(e) => setOriginStation(e.target.value)}>
          <option value="">--Source Station--</option>
          {stations.map((station) => (
            <option key={station.StationID} value={station.StationName}>
              {station.StationName}
            </option>
          ))}
        </select>

        <select value={destinationStation} onChange={(e) => setDestinationStation(e.target.value)}>
          <option value="">--Destination Station--</option>
          {stations.map((station) => (
            <option key={station.StationID} value={station.StationName}>
              {station.StationName}
            </option>
          ))}
        </select>

        <input type="date" value={travelDate} onChange={(e) => setTravelDate(e.target.value)} />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
        {error && <p className="error">{error}</p>}
      </div>

      {/* Train Stops Table */}
      {trainStops.length > 0 && (
        <div className="results-container">
          <h3>Train Stops</h3>
          {trainStops.map((line, index) => (
            <div key={index} className="train-line">
              <h4>
                Transit Line: {line.TransitLineName} (Train ID: {line.TrainID})
              </h4>
              <table className="schedules-table">
                <thead>
                  <tr>
                    <th>Station Name</th>
                    <th>Arrival Time</th>
                    <th>Departure Time</th>
                  </tr>
                </thead>
                <tbody>
                  {line.Stops.map((stop, idx) => (
                    <tr key={idx}>
                      <td>{stop.StationName}</td>
                      <td>{new Date(stop.ArrivalDateTime).toLocaleString()}</td>
                      <td>{new Date(stop.DepartureDateTime).toLocaleString()}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MainPage;
