import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';

const ReplyQuestionsTab = () => {
  const [questions, setQuestions] = useState([]);
  const [error, setError] = useState('');
  const [reply, setReply] = useState('');
  const [activeQuestionId, setActiveQuestionId] = useState(null);

  // Fetch questions on component mount
  useEffect(() => {
    fetchQuestions();
  }, []);

  // Function to fetch customer questions
  const fetchQuestions = async () => {
    try {
      const response = await axiosInstance.get('/representative/questions');
      setQuestions(response.data.questions);
      setError('');
    } catch (err) {
      console.error('Error fetching questions:', err);
      setError('Failed to fetch questions.');
    }
  };

  // Handle reply to a question
  const handleReplyChange = (e) => {
    setReply(e.target.value);
  };

  const submitReply = async (questionId) => {
    try {
      await axiosInstance.put(`/representative/questions/${questionId}`, { reply });
      setReply('');
      setActiveQuestionId(null);
      fetchQuestions(); // Refresh questions list to show updated replies
    } catch (err) {
      console.error('Error submitting reply:', err);
      setError('Failed to submit reply.');
    }
  };

  // Render
  return (
    <div>
      <h2>Reply to Customer Questions</h2>
      {error && <p className="error">{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Question ID</th>
            <th>Customer Name</th>
            <th>Question</th>
            <th>Reply</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {questions.map((question) => (
            <tr key={question.QuestionID}>
              <td>{question.QuestionID}</td>
              <td>{`${question.CustomerFirstName} ${question.CustomerLastName}`}</td>
              <td>{question.Question}</td>
              <td>
                {question.IsAnswered ? (
                  question.Answer
                ) : (
                  activeQuestionId === question.QuestionID ? (
                    <textarea
                      value={reply}
                      onChange={handleReplyChange}
                      placeholder="Enter your reply here"
                    />
                  ) : (
                    'Pending Reply'
                  )
                )}
              </td>
              <td>
                {question.IsAnswered ? (
                  <p>Replied</p>
                ) : (
                  activeQuestionId === question.QuestionID ? (
                    <>
                      <button onClick={() => submitReply(question.QuestionID)}>Submit</button>
                      <button onClick={() => setActiveQuestionId(null)}>Cancel</button>
                    </>
                  ) : (
                    <button onClick={() => setActiveQuestionId(question.QuestionID)}>Reply</button>
                  )
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ReplyQuestionsTab;
