import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";

const ManageRepresentatives = () => {
  const [representatives, setRepresentatives] = useState([]);
  const [newRep, setNewRep] = useState({ name: "", email: "", role: "Customer Representative" });
  const [editRep, setEditRep] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Fetch all representatives
  useEffect(() => {
    const fetchRepresentatives = async () => {
      try {
        const response = await axiosInstance.get("/admin/employees");
        setRepresentatives(response.data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch representatives.");
      }
    };
    fetchRepresentatives();
  }, []);

  // Handle input changes for adding/editing
  const handleInputChange = (e, isEdit = false) => {
    const { name, value } = e.target;
    if (isEdit) {
      setEditRep((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewRep((prev) => ({ ...prev, [name]: value }));
    }
  };

  // Add a new representative
  const handleAddRep = async () => {
    try {
      const response = await axiosInstance.post("/admin/employees", newRep);
      setRepresentatives((prev) => [...prev, response.data.newEmployee]);
      setNewRep({ name: "", email: "", role: "Customer Representative" });
      setSuccess("Representative added successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to add representative.");
    }
  };

  // Edit a representative
  const handleEditRep = async () => {
    try {
      await axiosInstance.put(`/admin/employees/${editRep.id}`, editRep);
      setRepresentatives((prev) =>
        prev.map((rep) => (rep.id === editRep.id ? editRep : rep))
      );
      setEditRep(null);
      setSuccess("Representative updated successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to update representative.");
    }
  };

  // Delete a representative
  const handleDeleteRep = async (id) => {
    if (!window.confirm("Are you sure you want to delete this representative?")) return;

    try {
      await axiosInstance.delete(`/admin/employees/${id}`);
      setRepresentatives((prev) => prev.filter((rep) => rep.id !== id));
      setSuccess("Representative deleted successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to delete representative.");
    }
  };

  return (
    <div>
      <h1>Manage Customer Representatives</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {representatives.map((rep) => (
            <tr key={rep.id}>
              <td>{rep.id}</td>
              <td>{rep.name}</td>
              <td>{rep.email}</td>
              <td>{rep.role}</td>
              <td>
                <button onClick={() => setEditRep(rep)}>Edit</button>
                <button onClick={() => handleDeleteRep(rep.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editRep && (
        <div>
          <h2>Edit Representative</h2>
          <input
            type="text"
            name="name"
            value={editRep.name}
            onChange={(e) => handleInputChange(e, true)}
          />
          <input
            type="email"
            name="email"
            value={editRep.email}
            onChange={(e) => handleInputChange(e, true)}
          />
          <button onClick={handleEditRep}>Update</button>
          <button onClick={() => setEditRep(null)}>Cancel</button>
        </div>
      )}

      <h2>Add New Representative</h2>
      <input
        type="text"
        name="name"
        value={newRep.name}
        placeholder="Name"
        onChange={handleInputChange}
      />
      <input
        type="email"
        name="email"
        value={newRep.email}
        placeholder="Email"
        onChange={handleInputChange}
      />
      <button onClick={handleAddRep}>Add</button>
    </div>
  );
};

export default ManageRepresentatives;
