import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";

const ManageRepresentatives = () => {
  const [representatives, setRepresentatives] = useState([]);
  const [newRep, setNewRep] = useState({
    ssn: "",
    firstName: "",
    lastName: "",
    username: "",
    password: "",
    employeeType: "Customer Representative",
  });
  const [editRep, setEditRep] = useState(null);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Fetch all representatives
  useEffect(() => {
    const fetchRepresentatives = async () => {
      try {
        const response = await axiosInstance.get("/admin/employees");
        setRepresentatives(response.data.employees || []);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch representatives.");
      }
    };
    fetchRepresentatives();
  }, []);

  // Handle input changes for adding/editing
  const handleInputChange = (e, isEdit = false) => {
    const { name, value } = e.target;
    if (isEdit) {
      setEditRep((prev) => ({ ...prev, [name]: value }));
    } else {
      setNewRep((prev) => ({ ...prev, [name]: value }));
    }
  };

  // Handle add representative
  const handleAddRep = async () => {
    const trimmedRep = {
      ssn: newRep.ssn.trim(),
      firstName: newRep.firstName.trim(),
      lastName: newRep.lastName.trim(),
      username: newRep.username.trim(),
      password: newRep.password.trim(),
      employeeType: newRep.employeeType.trim(),
    };

    // Check if all fields are filled
    if (
      !trimmedRep.ssn ||
      !trimmedRep.firstName ||
      !trimmedRep.lastName ||
      !trimmedRep.username ||
      !trimmedRep.password ||
      !trimmedRep.employeeType
    ) {
      setError("All fields are required.");
      return;
    }

    // Validate employee type
    if (!["Admin", "Customer Representative"].includes(trimmedRep.employeeType)) {
      setError("Employee type must be either 'Admin' or 'Customer Representative'.");
      return;
    }

    try {
      const response = await axiosInstance.post("/admin/employees", trimmedRep);
      setRepresentatives((prev) => [...prev, response.data.newEmployee]);
      setNewRep({
        ssn: "",
        firstName: "",
        lastName: "",
        username: "",
        password: "",
        employeeType: "Customer Representative",
      });
      setError("");
      setSuccess("Representative added successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to add representative.");
    }
  };



  // Edit a representative
  const handleEditRep = async () => {
    try {
      await axiosInstance.put(`/admin/employees/${editRep.id}`, editRep);
      setRepresentatives((prev) =>
        prev.map((rep) => (rep.id === editRep.id ? editRep : rep))
      );
      setEditRep(null);
      setSuccess("Representative updated successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to update representative.");
    }
  };

  // Delete a representative
  const handleDeleteRep = async (id) => {
    if (!window.confirm("Are you sure you want to delete this representative?")) return;

    try {
      await axiosInstance.delete(`/admin/employees/${id}`);
      setRepresentatives((prev) => prev.filter((rep) => rep.id !== id));
      setSuccess("Representative deleted successfully.");
    } catch (err) {
      console.error(err);
      setError("Failed to delete representative.");
    }
  };

  return (
    <div>
      <h1>Manage Customer Representatives</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {success && <p style={{ color: "green" }}>{success}</p>}

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
  {representatives.map((rep) => (
    <tr key={rep.EmpID}>
      <td>{rep.EmpID}</td>
      <td>{rep.FirstName}</td>
      <td>{rep.LastName}</td>
      <td>{rep.EmployeeType}</td>
              <td>
                <button onClick={() => setEditRep(rep)}>Edit</button>
                <button onClick={() => handleDeleteRep(rep.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editRep && (
        <div>
          <h2>Edit Representative</h2>
          <input
            type="text"
            name="name"
            value={editRep.name}
            onChange={(e) => handleInputChange(e, true)}
          />
          <input
            type="email"
            name="email"
            value={editRep.email}
            onChange={(e) => handleInputChange(e, true)}
          />
          <button onClick={handleEditRep}>Update</button>
          <button onClick={() => setEditRep(null)}>Cancel</button>
        </div>
      )}

<h2>Add New Representative</h2>
<form
  onSubmit={(e) => {
    e.preventDefault();
    handleAddRep();
  }}
>
  <input
    type="text"
    name="ssn"
    value={newRep.ssn}
    placeholder="SSN"
    onChange={(e) => handleInputChange(e)}
  />
  <input
    type="text"
    name="firstName"
    value={newRep.firstName}
    placeholder="First Name"
    onChange={(e) => handleInputChange(e)}
  />
  <input
    type="text"
    name="lastName"
    value={newRep.lastName}
    placeholder="Last Name"
    onChange={(e) => handleInputChange(e)}
  />
  <input
    type="text"
    name="username"
    value={newRep.username}
    placeholder="Username"
    onChange={(e) => handleInputChange(e)}
  />
  <input
    type="password"
    name="password"
    value={newRep.password}
    placeholder="Password"
    onChange={(e) => handleInputChange(e)}
  />
  <select
    name="employeeType"
    value={newRep.employeeType}
    onChange={(e) => handleInputChange(e)}
  >
    <option value="Customer Representative">Customer Representative</option>
    <option value="Admin">Admin</option>
  </select>
  <button type="submit">Add</button>
</form>

    </div>
  );
};

export default ManageRepresentatives;
