// src/pages/ManageReservations.js
import React, { useState, useEffect } from 'react';
import axiosInstance from '../utils/axiosInstance';

const ManageReservations = () => {
  const [reservations, setReservations] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchReservations();
  }, []);

  const fetchReservations = async (transitLine = '', customerName = '') => {
    try {
      const [reservationsResponse, customersResponse, transitLinesResponse] = await Promise.all([
        axiosInstance.get('/admin/reservations', {
          params: {
            transitLine: transitLine || undefined,
            customerName: customerName || undefined,
          },
        }),
        axiosInstance.get('/admin/customers'),
        axiosInstance.get('/admin/transit-lines'),
      ]);

      const customers = customersResponse.data.customers.reduce((acc, customer) => {
        acc[customer.CustomerID] = `${customer.FirstName} ${customer.LastName}`;
        return acc;
      }, {});

      const transitLines = transitLinesResponse.data.reduce((acc, line) => {
        acc[line.TransitLineName] = line.TransitLineName;
        return acc;
      }, {});

      const reservations = reservationsResponse.data.reservations.map((reservation) => ({
        ...reservation,
        Customer: customers[reservation.CustomerID] || 'Unknown Customer',
        TransitLine: transitLines[reservation.ScheduleID] || 'Unknown Line',
      }));

      setReservations(reservations);
    } catch (err) {
      setError('Failed to fetch reservations.');
    }
  };



  const deleteReservation = async (reservationId) => {
    try {
      await axiosInstance.delete(`/admin/reservations/${reservationId}`);
      setReservations(reservations.filter(r => r.ReservationID !== reservationId));
    } catch (err) {
      setError('Failed to delete reservation.');
    }
  };

  return (
    <div>
      <h2>Manage Reservations</h2>
      {error && <p className="error">{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Reservation ID</th>
            <th>Customer ID</th>
            <th>Schedule ID</th>
            <th>Number of Tickets</th>
            <th>Total Fare</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((res) => (
            <tr key={res.ReservationID}>
              <td>{res.ReservationID}</td>
              <td>{res.CustomerID}</td>
              <td>{res.ScheduleID}</td>
              <td>{res.NumberOfTickets}</td>
              <td>{res.TotalFare}</td>
              <td>
                <button onClick={() => deleteReservation(res.ReservationID)}>Delete</button>
                {/* Add Edit button if needed */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageReservations;
