import React, { useState, useEffect } from "react";
import axiosInstance from "../utils/axiosInstance";

const ManageTrainSchedules = () => {
  const [trainSchedules, setTrainSchedules] = useState([]);
  const [newSchedule, setNewSchedule] = useState({
    trainID: "",
    transitLineName: "",
    departureDateTime: "",
    arrivalDateTime: "",
    travelTime: "", // Add travelTime to the state
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchTrainSchedules = async () => {
      try {
        const response = await axiosInstance.get("/admin/train-schedules");
        if (response.data && Array.isArray(response.data.trainSchedules)) {
          setTrainSchedules(response.data.trainSchedules);
        } else {
          setTrainSchedules([]);
          setError("No train schedules available.");
        }
      } catch (err) {
        console.error("Error fetching train schedules:", err);
        setError("Failed to fetch train schedules. Try again later.");
      }
    };
    fetchTrainSchedules();
  }, []);

  // Function to calculate travelTime
  const calculateTravelTime = (departureTime, arrivalTime) => {
    if (!departureTime || !arrivalTime) return "";

    const depTime = new Date(`1970-01-01T${departureTime}:00`);
    const arrTime = new Date(`1970-01-01T${arrivalTime}:00`);

    // Adjust for cases where arrival is on the next day
    if (arrTime < depTime) {
      arrTime.setDate(arrTime.getDate() + 1);
    }

    const diffMs = arrTime - depTime; // Difference in milliseconds
    const diffHrs = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMins = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));

    return `${diffHrs.toString().padStart(2, "0")}:${diffMins.toString().padStart(2, "0")}:00`;
  };

  const handleAddSchedule = async () => {
    try {
      const travelTime = calculateTravelTime(newSchedule.departureTime, newSchedule.arrivalTime);
      const response = await axiosInstance.post("/admin/train-schedules", {
        ...newSchedule,
        travelTime, // Include the calculated travelTime
      });
      if (response.data) {
        setTrainSchedules([...trainSchedules, response.data.newSchedule]);
        setNewSchedule({
          trainID: "",
          transitLineName: "",
          departureTime: "",
          arrivalTime: "",
          travelTime: "",
        });
      }
    } catch (err) {
      console.error("Error adding train schedule:", err);
      setError("Failed to add train schedule. Try again later.");
    }
  };

  return (
    <div>
      <h1>Manage Train Schedules</h1>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <table>
        <thead>
          <tr>
            <th>Schedule ID</th>
            <th>Train ID</th>
            <th>Transit Line Name</th>
            <th>Departure Time</th>
            <th>Arrival Time</th>
            <th>Travel Time</th>
          </tr>
        </thead>
        <tbody>
          {trainSchedules.map((schedule) => (
            <tr key={schedule.ScheduleID}>
              <td>{schedule.ScheduleID}</td>
              <td>{schedule.TrainID}</td>
              <td>{schedule.TransitLineName}</td>
              <td>{schedule.DepartureTime}</td>
              <td>{schedule.ArrivalTime}</td>
              <td>{schedule.TravelTime}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Add New Train Schedule</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleAddSchedule();
        }}
      >
        <input
          type="text"
          placeholder="Train ID"
          value={newSchedule.trainID}
          onChange={(e) => setNewSchedule({ ...newSchedule, trainID: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Transit Line Name"
          value={newSchedule.transitLineName}
          onChange={(e) => setNewSchedule({ ...newSchedule, transitLineName: e.target.value })}
          required
        />
        <input
          type="time"
          placeholder="Departure Time"
          value={newSchedule.departureTime}
          onChange={(e) => {
            const departureTime = e.target.value;
            const travelTime = calculateTravelTime(departureTime, newSchedule.arrivalTime);
            setNewSchedule({ ...newSchedule, departureTime, travelTime });
          }}
          required
        />
        <input
          type="time"
          placeholder="Arrival Time"
          value={newSchedule.arrivalTime}
          onChange={(e) => {
            const arrivalTime = e.target.value;
            const travelTime = calculateTravelTime(newSchedule.departureTime, arrivalTime);
            setNewSchedule({ ...newSchedule, arrivalTime, travelTime });
          }}
          required
        />
        <p>Travel Time: {newSchedule.travelTime || "00:00:00"}</p>
        <button type="submit">Add Schedule</button>
      </form>
    </div>
  );
};

export default ManageTrainSchedules;
